function initSlick() {
    let width = window.innerWidth;
    console.log("画面幅:"+width);
    let slidesToShow_change = 1;
    if (width >= 1023) {
       slidesToShow_change = 3;
    } else {
        slidesToShow_change = 1;
    }  
    $(".slide-items").slick({
        autoplay: true, //自動再生
        autoplaySpeed: 4000, //スライドスピード
        slidesToShow: slidesToShow_change, // 表示するスライド枚数
        slidesToScroll: 1,// スライドする数
        infinite: true, //自動再生永遠
        dots: true, // インジケーター
    });
  }
  
  initSlick(); // 最初の初期化
  
  // 画面幅が変更された場合に再度初期化する
  window.addEventListener('resize', function () {
    $(".slide-items").slick('unslick'); // Slickを削除する
    initSlick(); // 再度初期化する
  });
  